import { SKIP } from './test-status';
import { runner } from './default-runner';


export let test = (name, func) => runner.addTest(name, func);

export let suite = (name, func) => runner.addSuite(name, func);

export let setup = (func) => runner.addBeforeTestCallback(func);

export let teardown = (func) => runner.addAfterTestCallback(func);

export let suiteSetup = (func) => runner.addBeforeCallback(func);

export let suiteTeardown = (func) => runner.addAfterCallback(func);

test.skip = (name, func) => runner.addTest(name, func, SKIP);

test.only = (name, func) => runner.addTestOnly(name, func);

suite.skip = (name, func) => runner.addSuite(name, func, SKIP);

suite.only = (name, func) => runner.addSuiteOnly(name, func);
